import LevelBuilderfrom sprites import *def render(name,bg):	lb = LevelBuilder.LevelBuilder(name+".plist",background=bg)	lb.addObject(Star.StarSprite(x=240, y=299,width=32,height=32))	lb.addObject(Nut.NutSprite(x=241, y=188,width=15,height=15,eventName='onNutHit' ,restitution=0.2,static='true',friction=0.5,density=20 ).setName('Nut'))	lb.addObject(Joints.RevoluteJoint(body1='Nut',body2='Star',motor_speed='100',enable_motor='true',torque='1000',lower_angle='12',upper_angle='50',enable_limit='false',collide_connected='false'))	lb.addObject(Hero.HeroSprite(x=86, y=16,width=32,height=32))	lb.addObject(Bomb.BombSprite(x=141, y=16,width=32,height=32 ,restitution=0.2,static='false',friction=0.5,density=20 ))	lb.addObject(Bomb.BombSprite(x=28, y=16,width=32,height=32 ,restitution=0.2,static='false',friction=0.5,density=20 ))	lb.addObject(Beam.BeamSprite(x=84, y=40,width=127,height=14,angle='0' ,restitution=0.2,static='false',friction=0.5,density=20 ))	lb.addObject(Enemy.EnemySprite(x=85, y=65,width=32,height=32,angle='0',restitution=0.2,static='false',friction=0.5,density=20 ))	lb.render()